import 'package:flutter/material.dart';import 'package:gos_uslugi/screens/payments/payment_history.dart';import 'package:gos_uslugi/screens/payments/pending_payment.dart';import 'package:gos_uslugi/screens/payments/widgets/tab_bar_widget.dart';import 'package:gos_uslugi/utils/constants/dependencies.dart';class PaymentView extends StatelessWidget {  const PaymentView({super.key});  @override  Widget build(BuildContext context) {    return const Scaffold(      body: Padding(          padding: EdgeInsets.symmetric(              horizontal: AppSizes.paddingHorizontal,              vertical: AppSizes.paddingVertical),          child: SafeArea(              child: Column(            crossAxisAlignment: CrossAxisAlignment.start,            children: [              Text('Платежи',                  style: TextStyle(fontSize: 30, fontWeight: FontWeight.bold)),              SizedBox(height: AppSizes.sizedBoxMd),              DefaultTabController(                  length: 2,                  child: Expanded(                      child: Column(                    children: [                      TabBarWidget(tabCount: 2),                      Expanded(                          child: TabBarView(                        children: [                          PendingPayment(),                          PaymentHistory(),                        ],                      ))                    ],                  ))),            ],          ))),    );  }}